import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: [],
        servicios: [],
    });

    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    // eslint-disable-next-line
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductoChange = (index, e) => {
        const { name, value } = e.target;
        const nuevosProductos = [...nuevoPedido.productos];
        nuevosProductos[index][name] = value;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: nuevosProductos,
        }));
    };

    const agregarProducto = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: [...prevPedido.productos, {
                nombre_producto: '',
                cantidad_producto: 0,
                precio_producto: 0,
            }],
        }));
    };

    const handleServicioChange = (index, e) => {
        const { name, value } = e.target;
        const nuevosServicios = [...nuevoPedido.servicios];
        nuevosServicios[index][name] = value;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: nuevosServicios,
        }));
    };

    const agregarServicio = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: [...prevPedido.servicios, {
                nombre_servicio: '',
                cantidad_servicio: 0,
                precio_servicio: 0,
            }],
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data);
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: [],
                servicios: [],
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            {/* Resto del formulario como antes */}

            <h2>Productos</h2>
            {nuevoPedido.productos.map((producto, index) => (
                <div key={index}>
                    <label>Nombre Producto:</label>
                    <input
                        type="text"
                        name={`productos[${index}].nombre_producto`}
                        value={producto.nombre_producto}
                        onChange={(e) => handleProductoChange(index, e)}
                    />
                    <label>Cantidad:</label>
                    <input
                        type="number"
                        name={`productos[${index}].cantidad_producto`}
                        value={producto.cantidad_producto}
                        onChange={(e) => handleProductoChange(index, e)}
                    />
                    <label>Precio:</label>
                    <input
                        type="number"
                        name={`productos[${index}].precio_producto`}
                        value={producto.precio_producto}
                        onChange={(e) => handleProductoChange(index, e)}
                    />
                </div>
            ))}
            <button type="button" onClick={agregarProducto}>Agregar Producto</button>

            <h2>Servicios</h2>
            {nuevoPedido.servicios.map((servicio, index) => (
                <div key={index}>
                    <label>Nombre Servicio:</label>
                    <input
                        type="text"
                        name={`servicios[${index}].nombre_servicio`}
                        value={servicio.nombre_servicio}
                        onChange={(e) => handleServicioChange(index, e)}
                    />
                    <label>Cantidad:</label>
                    <input
                        type="number"
                        name={`servicios[${index}].cantidad_servicio`}
                        value={servicio.cantidad_servicio}
                        onChange={(e) => handleServicioChange(index, e)}
                    />
                    <label>Precio:</label>
                    <input
                        type="number"
                        name={`servicios[${index}].precio_servicio`}
                        value={servicio.precio_servicio}
                        onChange={(e) => handleServicioChange(index, e)}
                    />
                </div>
            ))}
            <button type="button" onClick={agregarServicio}>Agregar Servicio</button>

            {/* Resto del formulario como antes */}
        </form>
    );
};

export default NuevoPedidoForm;
