import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: null, // Almacena el producto seleccionado
        servicios: null, // Almacena el servicio seleccionado
    });

    const productosOptions = [
        { value: 'arrayMediasEscolares', label: 'Medias Escolares' },
        { value: 'arrayCamisetaBasica', label: 'Camiseta Básica' },
        // Agrega más opciones según sea necesario
    ];

    const serviciosOptions = [
        { value: 'arrayMunecoPorcelanicron', label: 'Muñeco de Porcelanicron' },
        { value: 'arrayEntubarPantalon', label: 'Entubar Pantalón' },
        // Agrega más opciones según sea necesario
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductosChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: selectedOption,
        }));
    };

    const handleServiciosChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: selectedOption,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Aquí puedes acceder a la información seleccionada en nuevoPedido.productos y nuevoPedido.servicios
            // Realizar la solicitud POST a la API para agregar un nuevo pedido
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data); // Llama a la función proporcionada para actualizar el estado de pedidos en la lista
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: null,
                servicios: null,
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            {/* Otros campos del formulario */}
            
            <label>Productos:</label>
            <Select
                options={productosOptions}
                value={nuevoPedido.productos}
                onChange={handleProductosChange}
            />

            <label>Servicios:</label>
            <Select
                options={serviciosOptions}
                value={nuevoPedido.servicios}
                onChange={handleServiciosChange}
            />

            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
