import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: [], // Cambié a un array para permitir múltiples productos
        servicios: [], // Cambié a un array para permitir múltiples servicios
    });

    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleAgregarProducto = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: [
                ...prevPedido.productos,
                {
                    tipo_producto: {
                        nombre_tipo_producto: '',
                        estado_tipo_producto: '',
                    },
                    nombre_producto: '',
                    estado_producto: '',
                    cantidad_producto: 0,
                    precio_producto: 0,
                    estado_producto_catalogo: '',
                    subtotal: 0,
                },
            ],
        }));
    };

    const handleAgregarServicio = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: [
                ...prevPedido.servicios,
                {
                    tipo_servicio: {
                        nombre_tipo_servicio: '',
                        estado_tipo_servicio: '',
                    },
                    nombre_servicio: '',
                    estado_servicio: '',
                    cantidad_servicio: 0,
                    precio_servicio: 0,
                    estado_servicio_catalogo: '',
                    subtotal: 0,
                },
            ],
        }));
    };

    const handleProductoChange = (index, e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: prevPedido.productos.map((producto, i) =>
                i === index ? { ...producto, [name]: value } : producto
            ),
        }));
    };

    const handleServicioChange = (index, e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: prevPedido.servicios.map((servicio, i) =>
                i === index ? { ...servicio, [name]: value } : servicio
            ),
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data);
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: [],
                servicios: [],
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            {/* ... (rest of the form) */}
            
            {/* Sección de productos */}
            <h2>Productos</h2>
            {nuevoPedido.productos.map((producto, index) => (
                <div key={index}>
                    {/* ... (input fields for each product property) */}
                </div>
            ))}
            <button type="button" onClick={handleAgregarProducto}>
                Agregar Producto
            </button>

            {/* Sección de servicios */}
            <h2>Servicios</h2>
            {nuevoPedido.servicios.map((servicio, index) => (
                <div key={index}>
                    {/* ... (input fields for each service property) */}
                </div>
            ))}
            <button type="button" onClick={handleAgregarServicio}>
                Agregar Servicio
            </button>

            <br />
            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
