import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import Select from 'react-select';
import axios from 'axios';
import './App.css';

Modal.setAppElement('#root'); // Necesario para evitar errores de accesibilidad con react-modal

function App() {
  const [pedidos, setPedidos] = useState([]);
  const [selectedPedido, setSelectedPedido] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Obtener la lista de pedidos al cargar el componente
  useEffect(() => {
    axios.get('http://localhost:3000/pedidos')
      .then(response => {
        setPedidos(response.data);
      })
      .catch(error => {
        console.error('Error al obtener la lista de pedidos:', error);
      });
  }, []);

  // Abrir la modal y seleccionar el pedido al hacer clic en "ver detalle"
  const handleVerDetalle = (pedido) => {
    setSelectedPedido(pedido);
    setModalIsOpen(true);
  };

  // Manejar el cambio de estado en la modal
  const handleEstadoChange = (selectedOption) => {
    const newEstadoPedido = selectedOption.value;

    // Realizar una solicitud API para actualizar el estado en la base de datos
    axios.put(`http://localhost:3000/pedidos/${selectedPedido._id}`, { estado_pedido: newEstadoPedido })
      .then(response => {
        setPedidos(prevPedidos =>
          prevPedidos.map(pedido => (pedido._id === selectedPedido._id ? response.data : pedido))
        );
        setModalIsOpen(false);
      })
      .catch(error => {
        console.error('Error al actualizar el estado del pedido:', error);
      });
  };

  // Guardar los cambios en la modal
  const handleGuardarCambios = () => {
    // Aquí ya se maneja la actualización del estado en handleEstadoChange
  };

  return (
    <div>
      <h1>Listado de Pedidos</h1>
      <table>
        <thead>
          <tr>
            <th>Fecha Creación</th>
            <th>Fecha Pedido</th>
            <th>Total Pedido</th>
            <th>Estado Pedido</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map(pedido => (
            <tr key={pedido._id}>
              <td>{pedido.fecha_creacion}</td>
              <td>{pedido.fecha_pedido}</td>
              <td>{pedido.total_pedido}</td>
              <td>{pedido.estado_pedido}</td>
              <td>
                <button onClick={() => handleVerDetalle(pedido)}>Ver Detalle</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Detalles del Pedido"
      >
        {selectedPedido && (
          <div>
            <div style={{ display: 'flex', alignItems: 'center', flexDirection: 'column' }}>
              <h2>Detalles del Pedido</h2>
              <p>Fecha Creación: {selectedPedido.fecha_creacion}</p>
              <p>Fecha Pedido: {selectedPedido.fecha_pedido}</p>
              <p>Total Pedido: {selectedPedido.total_pedido}</p>
            </div>
            {/* Mostrar detalles del Servicio y Producto en un formato de cuadro */}
            {(selectedPedido.servicios || selectedPedido.productos) ? (
              <div style={{ display: 'flex', justifyContent: 'space-evenly' }}>
                {/* Detalles del Servicio */}
                {selectedPedido.servicios && (
                  <div>
                    <h3>Detalles del Servicio</h3>
                    {/* ... (resto del código) ... */}
                  </div>
                )}

                {/* Detalles del Producto */}
                {selectedPedido.productos && (
                  <div>
                    <h3>Detalles del Producto</h3>
                    {/* ... (resto del código) ... */}
                  </div>
                )}
              </div>
            ) : (
              <p>No hay detalles de servicio y producto disponibles.</p>
            )}

            <p>Estado Pedido:
              <Select
                options={[
                  { value: 'en proceso', label: 'En proceso' },
                  { value: 'por hacer', label: 'Por Hacer' },
                  { value: 'hecho', label: 'Hecho' },
                  { value: 'entregado', label: 'Entregado' },
                  { value: 'deshabilitado', label: 'Deshabilitado' },
                ]}
                value={{ value: selectedPedido.estado_pedido, label: selectedPedido.estado_pedido }}
                onChange={handleEstadoChange}
              />
            </p>

            <button onClick={handleGuardarCambios}>Guardar Cambios</button>
          </div>
        )}
      </Modal>
    </div>
  );
}

export default App;
