import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: null,
        servicios: null,
    });

    const [productosOptions, setProductosOptions] = useState([
        { value: 'arrayMediasEscolares', label: 'Medias Escolares' },
        { value: 'arrayCamisetaBasica', label: 'Camiseta Básica' },
    ]);

    const [serviciosOptions, setServiciosOptions] = useState([
        { value: 'arrayMunecoPorcelanicron', label: 'Muñeco de Porcelanicron' },
        { value: 'arrayEntubarPantalon', label: 'Entubar Pantalón' },
    ]);

    // Utiliza useEffect para actualizar la fecha_creacion al montar el componente
    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductosSelectChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: selectedOption,
        }));
    };

    const handleServiciosSelectChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: selectedOption,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Realizar la solicitud POST a la API para agregar un nuevo pedido
            const response = await axios.post('http://localhost:3000/pedidos', {
                ...nuevoPedido,
                productos: eval(nuevoPedido.productos.value),
                servicios: eval(nuevoPedido.servicios.value),
            });
            onPedidoAgregado(response.data);
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: null,
                servicios: null,
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            {/* Resto del formulario... */}

            <label>Productos:</label>
            <Select
                value={nuevoPedido.productos}
                onChange={handleProductosSelectChange}
                options={productosOptions}
                placeholder="Seleccione un producto"
            />

            <label>Servicios:</label>
            <Select
                value={nuevoPedido.servicios}
                onChange={handleServiciosSelectChange}
                options={serviciosOptions}
                placeholder="Seleccione un servicio"
            />

            {/* Resto del formulario... */}

            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
