import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [pedidos, setPedidos] = useState([]);
  const [error, setError] = useState(null);
  const [nuevoPedido, setNuevoPedido] = useState({
    servicio: {
      tipo_servicio: {
        nombre_tipo_servicio: '',
        estado_tipo_servicio: ''
      },
      nombre_servicio: '',
      estado_servicio: '',
      cantidad_servicio: 0,
      precio_servicio: 0,
      estado_servicio_catalogo: '',
      subtotal: 0
    },
    producto: {
      tipo_producto: {
        nombre_tipo_producto: '',
        estado_tipo_producto: ''
      },
      nombre_producto: '',
      estado_producto: '',
      cantidad_producto: 0,
      precio_producto: 0,
      estado_producto_catalogo: '',
      subtotal: 0
    },
    fecha_creacion: '',
    fecha_pedido: '',
    total_pedido: 0,
    estado_pedido: ''
  });
  const [pedidoEditando, setPedidoEditando] = useState(null);

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const response = await axios.get('http://localhost:3000/pedidos');
        setPedidos(response.data);
      } catch (error) {
        console.error('Error al obtener la lista de pedidos:', error);
        setError('Hubo un problema al obtener los pedidos.');
      }
    };

    fetchPedidos();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      [name]: value
    }));
  };

  const handleAgregarPedido = async () => {
    try {
      const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
      setPedidos([...pedidos, response.data]);
      setNuevoPedido({
        servicio: {
          tipo_servicio: {
            nombre_tipo_servicio: '',
            estado_tipo_servicio: ''
          },
          nombre_servicio: '',
          estado_servicio: '',
          cantidad_servicio: 0,
          precio_servicio: 0,
          estado_servicio_catalogo: '',
          subtotal: 0
        },
        producto: {
          tipo_producto: {
            nombre_tipo_producto: '',
            estado_tipo_producto: ''
          },
          nombre_producto: '',
          estado_producto: '',
          cantidad_producto: 0,
          precio_producto: 0,
          estado_producto_catalogo: '',
          subtotal: 0
        },
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: ''
      });
    } catch (error) {
      console.error('Error al agregar pedido:', error);
    }
  };

  const handleEliminarPedido = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/pedidos/${id}`);
      setPedidos(pedidos.filter((pedido) => pedido._id !== id));
    } catch (error) {
      console.error('Error al eliminar pedido:', error);
    }
  };

  const handleEditarPedido = (pedido) => {
    setPedidoEditando(pedido);
    setNuevoPedido({
      ...pedido
    });
  };

  const handleActualizarPedido = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/pedidos/${pedidoEditando._id}`, nuevoPedido);
      setPedidos(pedidos.map((pedido) => (pedido._id === pedidoEditando._id ? response.data : pedido)));
      setPedidoEditando(null);
      setNuevoPedido({
        servicio: {
          tipo_servicio: {
            nombre_tipo_servicio: '',
            estado_tipo_servicio: ''
          },
          nombre_servicio: '',
          estado_servicio: '',
          cantidad_servicio: 0,
          precio_servicio: 0,
          estado_servicio_catalogo: '',
          subtotal: 0
        },
        producto: {
          tipo_producto: {
            nombre_tipo_producto: '',
            estado_tipo_producto: ''
          },
          nombre_producto: '',
          estado_producto: '',
          cantidad_producto: 0,
          precio_producto: 0,
          estado_producto_catalogo: '',
          subtotal: 0
        },
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: ''
      });
    } catch (error) {
      console.error('Error al actualizar pedido:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        {error ? (
          <div>Error: {error}</div>
        ) : (
          <>
            <h2>Lista de Pedidos:</h2>
            <ul>
              {pedidos.map((pedido) => (
                <li key={pedido._id}>
                  Servicio: {pedido.servicio.nombre_servicio}, Producto: {pedido.producto.nombre_producto}, Estado: {pedido.estado_pedido}
                  <button type="button" onClick={() => handleEliminarPedido(pedido._id)}>
                    Eliminar
                  </button>
                  <button type="button" onClick={() => handleEditarPedido(pedido)}>
                    Editar
                  </button>
                </li>
              ))}
            </ul>
            {pedidoEditando ? (
              <>
                <h2>Editar Pedido:</h2>
                <form>
                  {/* Ajusta los campos según el modelo de pedido */}
                  <label>
                    Nombre Servicio:
                    <input
                      type="text"
                      name="servicio.nombre_servicio"
                      value={nuevoPedido.servicio.nombre_servicio}
                      onChange={handleInputChange}
                    />
                  </label>
                  <label>
                    Nombre Producto:
                    <input
                      type="text"
                      name="producto.nombre_producto"
                      value={nuevoPedido.producto.nombre_producto}
                      onChange={handleInputChange}
                    />
                  </label>
                  <label>
                    Estado:
                    <input
                      type="text"
                      name="estado_pedido"
                      value={nuevoPedido.estado_pedido}
                      onChange={handleInputChange}
                    />
                  </label>
                  {/* Agrega más campos según sea necesario */}
                  <button type="button" onClick={handleActualizarPedido}>
                    Actualizar Pedido
                  </button>
                </form>
              </>
            ) : (
              <>
                <h2>Agregar Nuevo Pedido:</h2>
                <form>
                  {/* Ajusta los campos según el modelo de pedido */}
                  <label>
                    Nombre Servicio:
                    <input
                      type="text"
                      name="servicio.nombre_servicio"
                      value={nuevoPedido.servicio.nombre_servicio}
                      onChange={handleInputChange}
                    />
                  </label>
                  <label>
                    Nombre Producto:
                    <input
                      type="text"
                      name="producto.nombre_producto"
                      value={nuevoPedido.producto.nombre_producto}
                      onChange={handleInputChange}
                    />
                  </label>
                  <label>
                    Estado:
                    <input
                      type="text"
                      name="estado_pedido"
                      value={nuevoPedido.estado_pedido}
                      onChange={handleInputChange}
                    />
                  </label>
                  {/* Agrega más campos según sea necesario */}
                  <button type="button" onClick={handleAgregarPedido}>
                    Agregar Pedido
                  </button>
                </form>
              </>
            )}
          </>
        )}
      </header>
    </div>
  );
}

export default App;
