// NuevoPedidoForm.js
import React, { useState } from 'react';
import axios from 'axios';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: '',
        productos: {
            tipo_producto: {
                nombre_tipo_producto: '',
                estado_tipo_producto: '',
            },
            nombre_producto: '',
            estado_producto: '',
            cantidad_producto: 0,
            precio_producto: 0,
            estado_producto_catalogo: '',
            subtotal: 0,
        },
        servicios: {
            tipo_servicio: {
                nombre_tipo_servicio: '',
                estado_tipo_servicio: '',
            },
            nombre_servicio: '',
            estado_servicio: '',
            cantidad_servicio: 0,
            precio_servicio: 0,
            estado_servicio_catalogo: '',
            subtotal: 0,
        },
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Realizar la solicitud POST a la API para agregar un nuevo pedido
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data); // Llama a la función proporcionada para actualizar el estado de pedidos en la lista
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: {
                    tipo_producto: {
                        nombre_tipo_producto: '',
                        estado_tipo_producto: '',
                    },
                    nombre_producto: '',
                    estado_producto: '',
                    cantidad_producto: 0,
                    precio_producto: 0,
                    estado_producto_catalogo: '',
                    subtotal: 0,
                },
                servicios: {
                    tipo_servicio: {
                        nombre_tipo_servicio: '',
                        estado_tipo_servicio: '',
                    },
                    nombre_servicio: '',
                    estado_servicio: '',
                    cantidad_servicio: 0,
                    precio_servicio: 0,
                    estado_servicio_catalogo: '',
                    subtotal: 0,
                },
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    // Utiliza useEffect para actualizar la fecha_creacion al montar el componente
  useEffect(() => {
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      fecha_creacion: new Date().toISOString().split('T')[0],
    }));
  }, []); // El segundo argumento es un array vacío para que se ejecute solo al montar el componente

    return (

        <form style={{ display: 'flex', alignItems: 'center', minHeight: '100%', flexDirection: 'column', justifyContent: 'center' }} onSubmit={handleSubmit}>
            <label>Fecha de Creación:</label>
            <input
                type="date"
                name="fecha_creacion"
                value={nuevoPedido.fecha_creacion}
                onChange={handleInputChange}
            />

            <label>Fecha de Pedido:</label>
            <input
                type="date"
                name="fecha_pedido"
                value={nuevoPedido.fecha_pedido}
                onChange={handleInputChange}
            />

            <label>Total Pedido:</label>
            <input
                type="number"
                name="total_pedido"
                value={nuevoPedido.total_pedido}
                onChange={handleInputChange}
            />

            <label>Producto - Nombre Tipo:</label>
            <input
                type="text"
                name="productos.tipo_producto.nombre_tipo_producto"
                value={nuevoPedido.productos.tipo_producto.nombre_tipo_producto}
                onChange={handleInputChange}
            />

            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
