import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: [], // Cambiado a un array para permitir varios productos
        servicios: [], // Cambiado a un array para permitir varios servicios
    });

    // Utiliza useEffect para actualizar la fecha_creacion al montar el componente
    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            // Establecer la fecha de pedido como mínimo 5 días después de la fecha de creación
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductoChange = (index, e) => {
        const { name, value } = e.target;
        const productos = [...nuevoPedido.productos];
        productos[index][name] = value;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos,
        }));
    };

    const handleServicioChange = (index, e) => {
        const { name, value } = e.target;
        const servicios = [...nuevoPedido.servicios];
        servicios[index][name] = value;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios,
        }));
    };

    const handleAgregarProducto = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: [...prevPedido.productos, {}],
        }));
    };

    const handleAgregarServicio = () => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: [...prevPedido.servicios, {}],
        }));
    };

    const handleQuitarProducto = (index) => {
        const productos = [...nuevoPedido.productos];
        productos.splice(index, 1);
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos,
        }));
    };

    const handleQuitarServicio = (index) => {
        const servicios = [...nuevoPedido.servicios];
        servicios.splice(index, 1);
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios,
        }));
    };

    const calcularTotalPedido = () => {
        const totalProductos = nuevoPedido.productos.reduce(
            (total, producto) => total + producto.cantidad_producto * producto.precio_producto,
            0
        );
        const totalServicios = nuevoPedido.servicios.reduce(
            (total, servicio) => total + servicio.cantidad_servicio * servicio.precio_servicio,
            0
        );
        return totalProductos + totalServicios;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const totalPedido = calcularTotalPedido();
            const response = await axios.post('http://localhost:3000/pedidos', {
                ...nuevoPedido,
                total_pedido: totalPedido,
            });
            onPedidoAgregado(response.data);
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: [],
                servicios: [],
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            {/* Resto del código permanece igual */}

            {/* Sección para agregar productos */}
            <div>
                <h2>Productos</h2>
                {nuevoPedido.productos.map((producto, index) => (
                    <div key={index}>
                        <label>Nombre del Producto:</label>
                        <input
                            type="text"
                            name={`productos[${index}].nombre_producto`}
                            value={producto.nombre_producto || ''}
                            onChange={(e) => handleProductoChange(index, e)}
                        />

                        {/* Resto de campos del producto, ajusta según tus necesidades */}

                        <button type="button" onClick={() => handleQuitarProducto(index)}>
                            Quitar Producto
                        </button>
                    </div>
                ))}
                <button type="button" onClick={handleAgregarProducto}>
                    Agregar Producto
                </button>
            </div>

            {/* Sección para agregar servicios */}
            <div>
                <h2>Servicios</h2>
                {nuevoPedido.servicios.map((servicio, index) => (
                    <div key={index}>
                        <label>Nombre del Servicio:</label>
                        <input
                            type="text"
                            name={`servicios[${index}].nombre_servicio`}
                            value={servicio.nombre_servicio || ''}
                            onChange={(e) => handleServicioChange(index, e)}
                        />

                        {/* Resto de campos del servicio, ajusta según tus necesidades */}

                        <button type="button" onClick={() => handleQuitarServicio(index)}>
                            Quitar Servicio
                        </button>
                    </div>
                ))}
                <button type="button" onClick={handleAgregarServicio}>
                    Agregar Servicio
                </button>
            </div>

            <label>Total Pedido:</label>
            <input type="number" name="total_pedido" value={calcularTotalPedido()} readOnly />
            <br />
            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
