import React, { useState, useEffect } from 'react';
import axios from 'axios';

const arrayMediasEscolares = {
  "tipo_producto": {
    "nombre_tipo_producto": "prenda de vestir",
    "estado_tipo_producto": "activo"
  },
  "nombre_producto": "medias escolares",
  "estado_producto": "activo",
  "cantidad_producto": 1,
  "precio_producto": 3000,
  "estado_producto_catalogo": "activo",
  "subtotal": 3000
};

const arrayCamisetaBasica = {
  "tipo_producto": {
    "nombre_tipo_producto": "prenda de vestir",
    "estado_tipo_producto": "activo"
  },
  "nombre_producto": "camiseta básica",
  "estado_producto": "activo",
  "cantidad_producto": 1,
  "precio_producto": 2500,
  "estado_producto_catalogo": "activo",
  "subtotal": 2500
};

const arrayMunecoPorcelanicron = {
  "tipo_servicio": {
    "nombre_tipo_servicio": "manualidades",
    "estado_tipo_servicio": "activo"
  },
  "nombre_servicio": "muñeco de porcelanicron",
  "estado_servicio": "activo",
  "cantidad_servicio": 1,
  "precio_servicio": 2000,
  "estado_servicio_catalogo": "activo",
  "subtotal": 2000
};

const arrayEntubarPantalon = {
  "tipo_servicio": {
    "nombre_tipo_servicio": "arreglo prenda de vestir",
    "estado_tipo_servicio": "activo"
  },
  "nombre_servicio": "entubar pantalon",
  "estado_servicio": "activo",
  "cantidad_servicio": 1,
  "precio_servicio": 1500,
  "estado_servicio_catalogo": "inactivo",
  "subtotal": 1500
};

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
  const [nuevoPedido, setNuevoPedido] = useState({
    fecha_creacion: '',
    fecha_pedido: '',
    total_pedido: 0,
    estado_pedido: 'por hacer',
    productos: null,
    servicios: null,
  });

  useEffect(() => {
    const fechaCreacionActual = new Date().toISOString().split('T')[0];
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      fecha_creacion: fechaCreacionActual,
      fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
        .toISOString()
        .split('T')[0],
    }));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      [name]: value,
    }));
  };

  const handleProductoChange = (selectedProducto) => {
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      productos: { ...selectedProducto }, // Copia profunda del objeto seleccionado
    }));
  };

  const handleServicioChange = (selectedServicio) => {
    setNuevoPedido((prevPedido) => ({
      ...prevPedido,
      servicios: { ...selectedServicio }, // Copia profunda del objeto seleccionado
    }));
  };

  const calculateTotalPedido = (productos, servicios) => {
    let total = 0;

    if (productos && productos.subtotal) {
      total += productos.subtotal;
    }

    if (servicios && servicios.subtotal) {
      total += servicios.subtotal;
    }

    return total;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const pedidoData = {
        fecha_creacion: nuevoPedido.fecha_creacion,
        fecha_pedido: nuevoPedido.fecha_pedido,
        total_pedido: nuevoPedido.total_pedido,
        estado_pedido: nuevoPedido.estado_pedido,
        producto: nuevoPedido.productos
          ? {
              cantidad_producto: nuevoPedido.productos.cantidad_producto,
              estado_producto: nuevoPedido.productos.estado_producto,
              estado_producto_catalogo: nuevoPedido.productos.estado_producto_catalogo,
              nombre_producto: nuevoPedido.productos.nombre_producto,
              precio_producto: nuevoPedido.productos.precio_producto,
              subtotal: nuevoPedido.productos.subtotal,
              tipo_producto: {
                estado_tipo_producto: nuevoPedido.productos.tipo_producto.estado_tipo_producto,
                nombre_tipo_producto: nuevoPedido.productos.tipo_producto.nombre_tipo_producto,
              },
            }
          : null,
        servicio: nuevoPedido.servicios
          ? {
              cantidad_servicio: nuevoPedido.servicios.cantidad_servicio,
              estado_servicio: nuevoPedido.servicios.estado_servicio,
              estado_servicio_catalogo: nuevoPedido.servicios.estado_servicio_catalogo,
              nombre_servicio: nuevoPedido.servicios.nombre_servicio,
              precio_servicio: nuevoPedido.servicios.precio_servicio,
              subtotal: nuevoPedido.servicios.subtotal,
              tipo_servicio: {
                estado_tipo_servicio: nuevoPedido.servicios.tipo_servicio.estado_tipo_servicio,
                nombre_tipo_servicio: nuevoPedido.servicios.tipo_servicio.nombre_tipo_servicio,
              },
            }
          : null,
      };
  
      // Agrega este console.log para imprimir los datos antes de enviar la solicitud
      console.log('Datos del pedido a enviar:', pedidoData);
  
      const response = await axios.post('http://localhost:3000/pedidos', pedidoData);
      onPedidoAgregado(response.data);
  
      setNuevoPedido({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: null,
        servicios: null,
      });
    } catch (error) {
      console.error('Error al agregar un nuevo pedido:', error);
    }
  };
  
  
  

  return (
    <form onSubmit={handleSubmit}>
      <label>Fecha de Creación:</label>
      <input
        type="date"
        name="fecha_creacion"
        value={nuevoPedido.fecha_creacion}
        onChange={handleInputChange}
        readOnly
      />

      <label>Fecha de Pedido:</label>
      <input
        type="date"
        name="fecha_pedido"
        value={nuevoPedido.fecha_pedido}
        onChange={handleInputChange}
        min={nuevoPedido.fecha_creacion}
      />

      <label>Total Pedido:</label>
      <input
        type="number"
        name="total_pedido"
        value={nuevoPedido.total_pedido}
        onChange={handleInputChange}
      />

      <div>
        <label>Producto:</label>
        <select onChange={(e) => handleProductoChange(JSON.parse(e.target.value))}>
          <option value="">Seleccione un producto</option>
          <option value={JSON.stringify(arrayMediasEscolares)}>Medias Escolares</option>
          <option value={JSON.stringify(arrayCamisetaBasica)}>Camiseta Básica</option>
        </select>
      </div>

      <div>
        <label>Servicio:</label>
        <select onChange={(e) => handleServicioChange(JSON.parse(e.target.value))}>
          <option value="">Seleccione un servicio</option>
          <option value={JSON.stringify(arrayMunecoPorcelanicron)}>Muñeco de Porcelanicron</option>
          <option value={JSON.stringify(arrayEntubarPantalon)}>Entubar Pantalón</option>
        </select>
      </div>

      <button type="submit">Agregar Pedido</button>
    </form>
  );
};

export default NuevoPedidoForm;
