import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: {
            nombre_producto: '',
            estado_producto: '',
            cantidad_producto: 0,
            precio_producto: 0,
            estado_producto_catalogo: '',
            subtotal: 0,
        },
        servicios: {
            nombre_servicio: '',
            estado_servicio: '',
            cantidad_servicio: 0,
            precio_servicio: 0,
            estado_servicio_catalogo: '',
            subtotal: 0,
        },
    });

    // Utiliza useEffect para actualizar la fecha_creacion al montar el componente
    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            // Establecer la fecha de pedido como mínimo 5 días después de la fecha de creación
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductoChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: {
                ...prevPedido.productos,
                [name]: value,
            },
        }));
    };

    const handleServicioChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: {
                ...prevPedido.servicios,
                [name]: value,
            },
        }));
    };

    const handleCantidadChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
        calcularTotalPedido(); // Recalcula el total al cambiar la cantidad
    };

    const calcularTotalPedido = () => {
        const subtotalProductos = nuevoPedido.productos.cantidad_producto * nuevoPedido.productos.precio_producto;
        const subtotalServicios = nuevoPedido.servicios.cantidad_servicio * nuevoPedido.servicios.precio_servicio;
        const totalPedido = subtotalProductos + subtotalServicios;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            total_pedido: totalPedido,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Realizar la solicitud POST a la API para agregar un nuevo pedido
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data); // Llama a la función proporcionada para actualizar el estado de pedidos en la lista
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: {
                    nombre_producto: '',
                    estado_producto: '',
                    cantidad_producto: 0,
                    precio_producto: 0,
                    estado_producto_catalogo: '',
                    subtotal: 0,
                },
                servicios: {
                    nombre_servicio: '',
                    estado_servicio: '',
                    cantidad_servicio: 0,
                    precio_servicio: 0,
                    estado_servicio_catalogo: '',
                    subtotal: 0,
                },
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            <label>Fecha de Creación:</label>
            <input
                type="date"
                name="fecha_creacion"
                value={nuevoPedido.fecha_creacion}
                onChange={handleInputChange}
                readOnly // Hace que el campo sea de solo lectura para que no se pueda editar
            />

            <label>Fecha de Pedido:</label>
            <input
                type="date"
                name="fecha_pedido"
                value={nuevoPedido.fecha_pedido}
                onChange={handleInputChange}
                min={nuevoPedido.fecha_creacion} // Establece la fecha mínima como la fecha de creación
            />

            <label>Total Pedido:</label>
            <input
                type="number"
                name="total_pedido"
                value={nuevoPedido.total_pedido}
                onChange={handleCantidadChange}
            />

            {/* Sección de Productos */}
            <h2>Productos</h2>
            <label>Nombre Producto:</label>
            <input
                type="text"
                name="nombre_producto"
                value={nuevoPedido.productos.nombre_producto}
                onChange={handleProductoChange}
            />

            <label>Estado Producto:</label>
            <input
                type="text"
                name="estado_producto"
                value={nuevoPedido.productos.estado_producto}
                onChange={handleProductoChange}
            />

            <label>Cantidad Producto:</label>
            <input
                type="number"
                name="cantidad_producto"
                value={nuevoPedido.productos.cantidad_producto}
                onChange={handleProductoChange}
            />

            <label>Precio Producto:</label>
            <input
                type="number"
                name="precio_producto"
                value={nuevoPedido.productos.precio_producto}
                onChange={handleProductoChange}
            />

            <label>Estado Producto Catálogo:</label>
            <input
                type="text"
                name="estado_producto_catalogo"
                value={nuevoPedido.productos.estado_producto_catalogo}
                onChange={handleProductoChange}
            />

            {/* Sección de Servicios */}
            <h2>Servicios</h2>
            <label>Nombre Servicio:</label>
            <input
                type="text"
                name="nombre_servicio"
                value={nuevoPedido.servicios.nombre_servicio}
                onChange={handleServicioChange}
            />

            <label>Estado Servicio:</label>
            <input
                type="text"
                name="estado_servicio"
                value={nuevoPedido.servicios.estado_servicio}
                onChange={handleServicioChange}
            />

            <label>Cantidad Servicio:</label>
            <input
                type="number"
                name="cantidad_servicio"
                value={nuevoPedido.servicios.cantidad_servicio}
                onChange={handleServicioChange}
            />

            <label>Precio Servicio:</label>
            <input
                type="number"
                name="precio_servicio"
                value={nuevoPedido.servicios.precio_servicio}
                onChange={handleServicioChange}
            />

            <label>Estado Servicio Catálogo:</label>
            <input
                type="text"
                name="estado_servicio_catalogo"
                value={nuevoPedido.servicios.estado_servicio_catalogo}
                onChange={handleServicioChange}
            />

            <br />
            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
