import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: null,
        servicios: null,
    });

    const [productosOptions, setProductosOptions] = useState([]);
    const [serviciosOptions, setServiciosOptions] = useState([]);

    useEffect(() => {
        // ... (tu código para cargar opciones de productos y servicios)
    }, []);

    useEffect(() => {
        const fechaCreacionActual = new Date().toISOString().split('T')[0];
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            fecha_creacion: fechaCreacionActual,
            fecha_pedido: new Date(new Date(fechaCreacionActual).getTime() + 5 * 24 * 60 * 60 * 1000)
                .toISOString()
                .split('T')[0],
        }));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleProductoChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: selectedOption?.value || null,
        }));
    };

    const handleServicioChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: selectedOption?.value || null,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Realizar la solicitud POST a la API para agregar un nuevo pedido
            const response = await axios.post('http://localhost:3000/pedidos', {
                ...nuevoPedido,
                // Envía solo el valor seleccionado, no el objeto completo
                productos: nuevoPedido.productos,
                servicios: nuevoPedido.servicios,
            });
            onPedidoAgregado(response.data); // Llama a la función proporcionada para actualizar el estado de pedidos en la lista
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: null,
                servicios: null,
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            <label>Fecha de Creación:</label>
            <input
                type="date"
                name="fecha_creacion"
                value={nuevoPedido.fecha_creacion}
                onChange={handleInputChange}
                readOnly
            />

            <label>Fecha de Pedido:</label>
            <input
                type="date"
                name="fecha_pedido"
                value={nuevoPedido.fecha_pedido}
                onChange={handleInputChange}
                min={nuevoPedido.fecha_creacion}
            />

<label>Seleccionar Producto:</label>
            <Select
                options={productosOptions}
                value={productosOptions.find((option) => option.value === nuevoPedido.productos)}
                onChange={handleProductoChange}
            />

            <label>Seleccionar Servicio:</label>
            <Select
                options={serviciosOptions}
                value={serviciosOptions.find((option) => option.value === nuevoPedido.servicios)}
                onChange={handleServicioChange}
            />


            <label>Total Pedido:</label>
            <input
                type="number"
                name="total_pedido"
                value={nuevoPedido.total_pedido}
                onChange={handleInputChange}
            />
            <br />
            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
