import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

const NuevoPedidoForm = ({ onPedidoAgregado }) => {
    const [nuevoPedido, setNuevoPedido] = useState({
        fecha_creacion: '',
        fecha_pedido: '',
        total_pedido: 0,
        estado_pedido: 'por hacer',
        productos: null,
        servicios: null,
    });

    const [productosOptions, setProductosOptions] = useState([]);
    const [serviciosOptions, setServiciosOptions] = useState([]);

    useEffect(() => {
        const productosOptionsData = [
            { value: 'arrayMediasEscolares', label: 'Medias Escolares' },
            { value: 'arrayCamisetaBasica', label: 'Camiseta Básica' },
            // Agregar más opciones según sea necesario
        ];

        const serviciosOptionsData = [
            { value: 'arrayMunecoPorcelanicron', label: 'Muñeco de Porcelanicron' },
            { value: 'arrayEntubarPantalon', label: 'Entubar Pantalón' },
            // Agregar más opciones según sea necesario
        ];

        setProductosOptions(productosOptionsData);
        setServiciosOptions(serviciosOptionsData);
    }, []);

    const handleProductoChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            productos: selectedOption,
        }));
    };

    const handleServicioChange = (selectedOption) => {
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            servicios: selectedOption,
        }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido((prevPedido) => ({
            ...prevPedido,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/pedidos', nuevoPedido);
            onPedidoAgregado(response.data);
            setNuevoPedido({
                fecha_creacion: '',
                fecha_pedido: '',
                total_pedido: 0,
                estado_pedido: 'por hacer',
                productos: null,
                servicios: null,
            });
        } catch (error) {
            console.error('Error al agregar un nuevo pedido:', error);
        }
    };

    return (
        <form
            style={{
                display: 'flex',
                alignItems: 'center',
                minHeight: '100%',
                flexDirection: 'column',
                justifyContent: 'center',
            }}
            onSubmit={handleSubmit}
        >
            <label>Fecha de Creación:</label>
            <input
                type="date"
                name="fecha_creacion"
                value={nuevoPedido.fecha_creacion}
                onChange={handleInputChange}
                readOnly
            />

            <label>Fecha de Pedido:</label>
            <input
                type="date"
                name="fecha_pedido"
                value={nuevoPedido.fecha_pedido}
                onChange={handleInputChange}
                min={nuevoPedido.fecha_creacion}
            />

            <label>Total Pedido:</label>
            <input
                type="number"
                name="total_pedido"
                value={nuevoPedido.total_pedido}
                onChange={handleInputChange}
            />

            <label>Seleccionar Producto:</label>
            <Select
                options={productosOptions}
                value={nuevoPedido.productos}
                onChange={handleProductoChange}
            />

            <label>Seleccionar Servicio:</label>
            <Select
                options={serviciosOptions}
                value={nuevoPedido.servicios}
                onChange={handleServicioChange}
            />

            <button type="submit">Agregar Pedido</button>
        </form>
    );
};

export default NuevoPedidoForm;
